import styled from '@emotion/styled'

const primaryColor = 'gray'

export const Button = styled.button`
  padding:10px;
  background-color:  #8CA5FF;
  font-size: 17px;
  border-radius: 4px;
  color: black;
  font-weight: bold;
  &:hover {
    color: white;
    background: #ef5357;
  }
`
export const Heading = styled.p`
  font-size: 17px;
  margin: 0px;
  color: gray;
  &:hover{
    cursor: pointer;
    color: black;
    transition: all linear 0.3s
  }
`
export const LandingPageContainer = styled.div`
 display: flex;
 flex-direction: column;
 width: 100%;
 align-items: center;
 border: 1px solid green;
 margin:0px;
`
