import styled from '@emotion/styled'

export const Input = styled.input`
font-size: 18px;
padding: 10px;
margin: 10px;
background: papayawhip;
border: none;
outline: none;
border-bottom: 1px solid red;
border-radius: 3px;
::placeholder {
  color: palevioletred;
}
`;
