import React, { createRef } from "react";
import Link from "next/link";
import { Nav } from "reactstrap";
import { routes } from "../dashboardRoutes";
import { useRouter } from "next/router";
import '../../../styles/sidebar/index.scss'

export const SideBar = () => {
  const sidebar = createRef();
  const router = useRouter();
  return (
    <div className="sidebar" data-color="red" data-active-color="white">

      <div className='sidebar-header logo'>
      <svg width="53" height="45" viewBox="0 0 53 45" fill="none" xmlns="http://www.w3.org/2000/svg">
<path d="M50.9421 19.6442L45.3587 20.2954V43.3768H40.9912H13.8771H9.509V19.6442H3.15533L26.7538 1.7101L33.3469 6.60064V1.7101H39.0051V10.8149L50.9421 19.6442Z" fill="#009999" stroke="#009999" stroke-width="1.5"/>
</svg>


        <a
          href="#"
          className="simple-text logo-normal"
          style={{ color: "black" }}
        >
          Community Dashboard
        </a>
      </div>

      <div className="sidebar-wrapper" ref={sidebar} style={{ color: "black" }}>
        <Nav>
          {routes.map((prop, key) => {
            return (
              <li key={key}>
                <Link
                  href="/dashboard/[user]/[role]"
                  as={`/dashboard/user/${prop.path}`}
                >
                  <div
                    className="nav-link"
                    style={
                      router.query.role === prop.path
                        ? {
                            background: "#F0F9F9",
                            marginTop: "25px",
                            display: "flex",alignItems:'center'
                          }
                        : { marginTop: "25px", display: "flex",alignItems:'center' }
                    }
                  >
                    {prop.icon}

                    <p
                      style={{
                        width: "100%",
                        height: "22px",
                        top: " calc(50% - 22px/2 + 2px)",
                        paddingLeft:'15px',
                        fontFamily: "Open Sans",
                        fontStyle: " normal",
                        fontWeight:( router.query.role === prop.path)? 650:'normal',
                        fontSize: "16px",
                        lineHeight: "22px",
                        color: '#344563',
                      }}
                    >
                      {prop.name}
                    </p>
                    {router.query.role === prop.path && (
                      <svg
                        width="10"
                        height="14"
                        viewBox="0 0 10 14"
                        fill="none"
                        xmlns="http://www.w3.org/2000/svg"
                      >
                        <path
                          d="M0.877747 1.90001L5.97775 7.00001L0.877747 12.1L2.45552 13.6667L9.12219 7.00001L2.45552 0.333344L0.877747 1.90001Z"
                          fill="#344563"
                        />
                      </svg>
                    )}
                  </div>
                </Link>
              </li>
            );
          })}
        </Nav>
      </div>
    </div>
  );
};
