import styled from '@emotion/styled'

const primaryColor = 'gray'

export const Button = styled.button`
  padding:8px;
  background-color:  #8CA5FF;
  font-size: 17px;
  border-radius: 4px;
  color: black;
  font-weight: bold;
  &:hover {
    color: white;
    background: #ef5357;
  }
`
export const Heading = styled.p`
  font-size: 17px;
  margin: 0px;
  ${(({color})=>{
return `
color: ${color?color:'gray'}
`

})}
  &:hover{
    cursor: pointer;
    color: black;
    transition: all linear 0.3s;
  }
  ${({style})=>{style}}
`
export const HeaderContainer = styled.div`
 display: flex;
 padding: 5px;
 width: 100%;
 height: 50px;
 justify-content: space-around;
 align-items: center;
`
